					    , TO_CHAR(SYSDATE,'YYYY-MM-DD')
					    , 'N'
					    ,#{tableName, jdbcType=VARCHAR}
					    ,#{status, jdbcType=VARCHAR}
					)

mapper.update:
	UPDATE NEXI_CHAT
	SET 
		  SUBJECT = #{subject, jdbcType=VARCHAR}
		, USERID = #{userid, jdbcType=VARCHAR}
		, IDATE = TO_CHAR(SYSDATE,'YYYY-MM-DD')
	WHERE SEQ = #{seq, jdbcType=VARCHAR}

UPDATE NEXI_CHAT
SET DELETEYN = 'Y'
WHERE SEQ = #{seq, jdbcType=VARCHAR}

